import React, { Component } from 'react';
import StickyHeader from 'react-sticky-header';
import title from '../Styles/Images/br√º.svg';
import { Button } from 'react-bootstrap';

export class TeacherDashboard extends Component {
	constructor(props) {
		super(props);
		this.state = {
			loginPopup: false,
			loginbtn: 'Login'
		};
	}

	togglePopup() {
		let login = 'Login';
		if (!this.state.showPopup) {
			login = 'Cancel';
		}
		this.setState({
			showPopup: !this.state.showPopup,
			loginbtn: login,
			errorLogin: 'Invalid Credentials'
		});
	}

	render() {
		return (
			<div>
				<StickyHeader
					header={
						<div className="Header-root">
							<div className="Home-navbar">
								<img src={title} className="Home-header-logo" style={{ height: '50px' }} alt="logo" />
								<div className="Home-header-txt">Home</div>
								<div className="Home-header-txt">About</div>
								<div className="Home-header-txt">Contact</div>
								<Button
									style={{ backgroundColor: 'black', width: '90px', height: '40px' }}
									onClick={this.togglePopup.bind(this)}
									block
									bsSize="small"
									type="button"
								>
									{this.state.loginbtn}
								</Button>
							</div>
						</div>
					}
				/>
			</div>
		);
	}
}

export default TeacherDashboard;
