const EditorConstants = {
	DEFAULT_STEP_NAME: "Untitled Step",
	DELETE_STEP_CONFIRMATION_MESSAGE: "Unable to delete! There must be at least one existing step in a lab.",
	DELETE_STEP_CONFIRMATION_TITLE: "Cannot Delete Step",
	ACTIONS: [
		{value: "Shake", label: "Shake"}, 
		{value: "Pour", label: "Pour"}, 
		{value: "Stir", label: "Stir"}, 
		{value: "Blend", label: "Blend"},
	],
	CONTEXT_MENU_ID: "tool-context-menu"
};

export default EditorConstants;
